import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [gender, setGender] = useState('');
  const [phoneNum, setPhoneNum] = useState('');
  const [dob, setDob] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [isHovered, setIsHovered] = useState(false);

  const handleSubmit = async () => {
    // Validate inputs
    if (!name || !username || !phoneNum || !dob || !email || !password || !confirmPassword) {
      setError('All fields are required');
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    const age = calculateAge(dob);
    if (age < 18) {
      setError('You must be at least 18 years old to register');
      return;
    }

    setError(''); // Clear errors

    const userData = {
      name,
      username,
      gender,
      phoneNum,
      dob,
      email,
      password,
    };

    try {
      const response = await axios.post('http://localhost:8000/api/signup/', userData);
      if (response.status === 201) {
        alert('Signup successful!');
        // Redirect to login or home page
      }
    } catch (err) {
      setError('Error during signup');
      console.error(err);
    }
  };

  const calculateAge = (dob) => {
    const birthDate = new Date(dob);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
    <div style={styles.page}>
      <main style={styles.main}>
        <div style={styles.container}>
          <h2>Create New Account</h2>
          <div style={styles.formGroup}>
            <label htmlFor="signupFullName">Name</label>
            <input
              id="signupFullName"
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="signupUsername">Username</label>
            <input
              id="signupUsername"
              type="text"
              placeholder="Enter your Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="GenderDropDown">Gender</label>
            <select
              id="GenderDropDown"
              value={gender}
              onChange={(e) => setGender(e.target.value)}
              style={styles.input}
            >
              <option value="" disabled>Select your Gender</option>
              <option value="M">Male</option>
              <option value="F">Female</option>
            </select>
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="signupPhoneNum">Phone Number</label>
            <input
              id="signupPhoneNum"
              type="tel"
              placeholder="Enter your Phone Number"
              value={phoneNum}
              onChange={(e) => setPhoneNum(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="DOB">Date of Birth</label>
            <input
              id="DOB"
              type="date"
              value={dob}
              onChange={(e) => setDob(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="signupEmail">Email</label>
            <input
              id="signupEmail"
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="signupPassword">Password</label>
            <input
              id="signupPassword"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label htmlFor="signupConfirmPassword">Confirm Password</label>
            <input
              id="signupConfirmPassword"
              type="password"
              placeholder="Confirm your password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              style={styles.input}
            />
          </div>

          <button
            style={isHovered ? { ...styles.button, ...styles.buttonHover } : styles.button}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            onClick={handleSubmit}
          >
            Signup
          </button>

          {error && <p style={styles.error}>{error}</p>}

          <p>Have an account? <a href="/login">LogIn</a></p>
        </div>
      </main>
    </div>
  );
};

// Inline CSS styles (same as before)
const styles = {
  // Your styles here...
};

export default Signup;
