import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './BookingPage.css';  // Make sure to import the CSS file

const BookingPage = () => {
  const { state } = useLocation(); // Retrieve state passed from TourCard component
  const { title, location, price, image } = state || {}; // Destructuring the state

  const navigate = useNavigate();

  // Form state to manage user input for booking
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    numberOfPeople: 1,
    specialRequests: ''
  });

  // If no state is passed or missing details, show an error message
  if (!title || !location || !price || !image) {
    return <h2>No details available for the selected tour. Please go back and select again.</h2>;
  }

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  // Handle form submission (Booking)
  const handleBooking = (e) => {
    e.preventDefault();
    alert('Your booking has been confirmed!');
    // Handle further actions like saving the booking details or sending an email confirmation.
    // Redirect the user to another page (optional)
    navigate('/');
  };

  return (
    <div className="booking-page" style={{ backgroundImage: `url(${image})` }}>
      <div className="form-overlay">
        <h1>Booking Details for {title}</h1>
        <div className="tour-info">
          <h2>{title}</h2>
          <p>{location}</p>
          <p>Price: {price} / day</p>
        </div>

        <form onSubmit={handleBooking} className="booking-form">
          <div className="form-group">
            <label htmlFor="name">Full Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="numberOfPeople">Number of People:</label>
            <input
              type="number"
              id="numberOfPeople"
              name="numberOfPeople"
              value={formData.numberOfPeople}
              onChange={handleInputChange}
              min="1"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="specialRequests">Special Requests:</label>
            <textarea
              id="specialRequests"
              name="specialRequests"
              value={formData.specialRequests}
              onChange={handleInputChange}
              rows="4"
            ></textarea>
          </div>

          <button type="submit" className="submit-btn">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
};

export default BookingPage;
