import React, { useState, useEffect } from 'react';
import './PaymentPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const [totalPrice, setTotalPrice] = useState(0);
  const [bookingDetails, setBookingDetails] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Retrieve booking details from sessionStorage
    const storedDetails = sessionStorage.getItem('bookingDetails');
    if (storedDetails) {
      const parsedDetails = JSON.parse(storedDetails);
      setBookingDetails(parsedDetails);
      const calculatedPrice = (location.state?.price || 0) * parsedDetails.numTickets;
      setTotalPrice(calculatedPrice);
    }
  }, [location.state]);

  const handlePayment = () => {
    // Simulate payment process
    alert(`Payment of $${totalPrice} successful!`);
    navigate('/confirmation'); // Redirect to a confirmation page
  };

  if (!bookingDetails) {
    return <h2>No booking details found. Please go back and book a tour first.</h2>;
  }

  return (
    <div className="payment-container">
      <h1 className="payment-title">Payment</h1>
      <div className="payment-card">
        <h2>{`Tour: ${location.state?.title || 'Unknown Tour'}`}</h2>
        <p>{`Number of Tickets: ${bookingDetails.numTickets}`}</p>
        <p>{`Total Price: $${totalPrice}`}</p>
        <button className="pay-btn" onClick={handlePayment}>
          Pay Now
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
