import React, { useState } from 'react';
import './BookingPage.css';
import { useNavigate } from 'react-router-dom';

const BookingPage = ({ tourDetails }) => {
  const [numTickets, setNumTickets] = useState(1);
  const [departureCity, setDepartureCity] = useState('');
  const navigate = useNavigate();

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    // Save booking details to sessionStorage or backend and redirect to Payment Page
    const bookingDetails = {
      tourId: tourDetails.TourId,
      departureCity,
      numTickets,
    };
    sessionStorage.setItem('bookingDetails', JSON.stringify(bookingDetails));
    navigate('/payment');
  };

  return (
    <div className="booking-container">
      <h1 className="booking-title">Book Your Tour</h1>
      <div className="booking-card">
        <h2>{tourDetails.TourName}</h2>
        <p>{`Destination: ${tourDetails.DestinationName}`}</p>
        <p>{`Price: $${tourDetails.Price} per person`}</p>
        <form onSubmit={handleBookingSubmit} className="booking-form">
          <label>
            Number of Tickets:
            <input
              type="number"
              min="1"
              value={numTickets}
              onChange={(e) => setNumTickets(e.target.value)}
              required
            />
          </label>
          <label>
            Departure City:
            <input
              type="text"
              value={departureCity}
              onChange={(e) => setDepartureCity(e.target.value)}
              placeholder="Enter departure city"
              required
            />
          </label>
          <button type="submit" className="book-btn">
            Confirm Booking
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingPage;
