import React, { useEffect, useState } from 'react';
import './PaymentPage.css';

const PaymentPage = () => {
  const [bookingDetails, setBookingDetails] = useState(null);

  useEffect(() => {
    const savedBookingDetails = JSON.parse(sessionStorage.getItem('bookingDetails'));
    if (savedBookingDetails) {
      setBookingDetails(savedBookingDetails);
    }
  }, []);

  if (!bookingDetails) {
    return <h2>No booking details found. Please go back and try again.</h2>;
  }

  return (
    <div className="payment-container">
      <h1 className="payment-title">Payment Details</h1>
      <div className="payment-card">
        <h2>{bookingDetails.type === 'trek' ? bookingDetails.trekId : bookingDetails.tourId}</h2>
        <p>{`Departure City: ${bookingDetails.departureCity}`}</p>
        <p>{`Number of Tickets: ${bookingDetails.numTickets}`}</p>
        <p>{`Price per Ticket: PKR ${bookingDetails.price}`}</p>
        <p>{`Total Price: PKR ${bookingDetails.totalPrice}`}</p>

        <button className="pay-btn">Proceed to Payment</button>
      </div>
    </div>
  );
};

export default PaymentPage;
