import React, { useState } from 'react';
import './TourGuide.css';

const TourGuide = () => {
  const [currentReview, setCurrentReview] = useState(0);
  const reviews = [
    "The guide was amazing, very knowledgeable!",
    "Excellent experience, highly recommended for any traveler.",
    "A fantastic tour! The guide made the trip unforgettable.",
    "Great guide! Friendly, informative, and fun.",
  ];

  const guide = {
    name: "John Doe",
    age: 30,
    rating: 4.9,
    image: "https://via.placeholder.com/150",
    tours: [
      {
        name: "Beach Adventure",
        price: "$200",
        details: "Explore the best beaches with a guided tour including activities and meals.",
      },
      {
        name: "Mountain Hiking",
        price: "$150",
        details: "Climb to breathtaking views, guided by our experienced mountain tour leaders.",
      },
    ],
  };

  const nextReview = () => {
    if (currentReview < reviews.length - 1) {
      setCurrentReview(currentReview + 1);
    } else {
      setCurrentReview(0); // Loop back to the first review
    }
  };

  const prevReview = () => {
    if (currentReview > 0) {
      setCurrentReview(currentReview - 1);
    } else {
      setCurrentReview(reviews.length - 1); // Loop to the last review
    }
  };

  return (
    <div className="tour-guide-container">
      <div className="guide-info">
        <img src={guide.image} alt={guide.name} className="guide-image" />
        <div className="guide-details">
          <h2>{guide.name}</h2>
          <p>Age: {guide.age}</p>
          <p>Rating: {guide.rating} ‚≠ê</p>
        </div>
      </div>

      <div className="reviews-container">
        <div className="reviews">
          <button className="arrow left" onClick={prevReview}>
            &lt;
          </button>
          <div className="review">{reviews[currentReview]}</div>
          <button className="arrow right" onClick={nextReview}>
            &gt;
          </button>
        </div>
      </div>

      <div className="pricing">
        <h3>Tour Packages:</h3>
        {guide.tours.map((tour, index) => (
          <div key={index} className="tour-package">
            <h4>{tour.name}</h4>
            <p>{tour.details}</p>
            <p className="price">{tour.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TourGuide;
