import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './BookingPage.css';

const BookingPage = () => {
  const { state } = useLocation();
  const [numTickets, setNumTickets] = useState(1);
  const [departureCity, setDepartureCity] = useState('');
  const navigate = useNavigate();

  if (!state) {
    return <h2>No booking details provided. Please go back and select a trek or tour.</h2>;
  }

  const { trekDetails, tourDetails, type } = state;

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const bookingDetails = {
      type, // Include type (trek or tour)
      departureCity,
      numTickets,
      price: type === 'trek' ? trekDetails.price : tourDetails.price,
      totalPrice: (type === 'trek' ? trekDetails.price : tourDetails.price) * numTickets,
    };
    sessionStorage.setItem('bookingDetails', JSON.stringify(bookingDetails));
    navigate('/payment');
  };

  const details = type === 'trek' ? trekDetails : tourDetails;

  return (
    <div className="booking-container">
      <h1 className="booking-title">Book Your {type === 'trek' ? 'Trek' : 'Tour'}</h1>
      <div className="booking-card">
        <h2>{details.title}</h2>
        <p>{`Duration: ${details.duration}`}</p>
        <p>{`Price: PKR ${details.price} per person`}</p>
        <form onSubmit={handleBookingSubmit} className="booking-form">
          <label>
            Number of Tickets:
            <input
              type="number"
              min="1"
              value={numTickets}
              onChange={(e) => setNumTickets(e.target.value)}
              required
            />
          </label>
          <label>
            Departure City:
            <input
              type="text"
              value={departureCity}
              onChange={(e) => setDepartureCity(e.target.value)}
              placeholder="Enter departure city"
              required
            />
          </label>
          <button type="submit" className="book-btn">Confirm Booking</button>
        </form>
      </div>
    </div>
  );
};

export default BookingPage;
