import React, { useState } from 'react';
import './BookingPage.css';
import { useNavigate, useLocation } from 'react-router-dom';

const BookingPage = () => {
  const [numTickets, setNumTickets] = useState(1);
  const [departureCity, setDepartureCity] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { state: tourDetails } = useLocation();

  if (!tourDetails) {
    return <h2 className="fallback-message">No tour details provided. Please go back and select a tour.</h2>;
  }

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const bookingDetails = {
      tourId: tourDetails.TourId || null,
      departureCity,
      numTickets,
    };
    sessionStorage.setItem('bookingDetails', JSON.stringify(bookingDetails));
  
    // Pass the required tour details in the state
    navigate('/payment', {
      state: {
        title: tourDetails.title,
        price: tourDetails.price,
      },
    });
  };
  

  return (
    <div className="booking-container">
      <h1 className="booking-title">Book Your Tour</h1>
      <div className="booking-card">
        {tourDetails.image && (
          <img
            src={tourDetails.image}
            alt={tourDetails.title}
            className="booking-image"
          />
        )}
        <h2>{tourDetails.title}</h2>
        <p>{`Location: ${tourDetails.location}`}</p>
        <p>{`Price: $${tourDetails.price} per person`}</p>
        <form onSubmit={handleBookingSubmit} className="booking-form">
          <label>
            Number of Tickets:
            <input
              type="number"
              min="1"
              value={numTickets}
              onChange={(e) => setNumTickets(e.target.value)}
              required
            />
          </label>
          <label>
            Departure City:
            <input
              type="text"
              value={departureCity}
              onChange={(e) => setDepartureCity(e.target.value)}
              placeholder="Enter departure city"
              required
            />
          </label>
          {error && <p className="error-message">{error}</p>}
          <button type="submit" className="book-btn">
            Confirm Booking
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingPage;



