import React, { useState, useEffect } from 'react';
import './PaymentPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [bookingDetails, setBookingDetails] = useState(null);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const storedDetails = sessionStorage.getItem('bookingDetails');
    const tourData = location.state;

    if (storedDetails && tourData) {
      const parsedDetails = JSON.parse(storedDetails);
      setBookingDetails(parsedDetails);

      // Calculate total price
      const calculatedPrice = tourData.price * parsedDetails.numTickets;
      setTotalPrice(calculatedPrice);
    }
  }, [location.state]);

  if (!bookingDetails || !location.state) {
    return (
      <div className="payment-container">
        <h2>No booking or tour details found. Please go back and book a tour first.</h2>
      </div>
    );
  }

  const handlePayment = () => {
    alert('Payment successful! Enjoy your trip.');
    navigate('/');
  };

  return (
    <div className="payment-container">
      <h1 className="payment-title">Confirm Your Payment</h1>
      <div className="payment-card">
        <h2>{location.state.title}</h2>
        <div className="payment-summary">
          <p><strong>Departure City:</strong> {bookingDetails.departureCity}</p>
          <p><strong>Number of Tickets:</strong> {bookingDetails.numTickets}</p>
          <p><strong>Price Per Ticket:</strong> ${location.state.price}</p>
          <p><strong>Total Price:</strong> ${totalPrice}</p>
        </div>
        <button className="pay-btn" onClick={handlePayment}>
          Confirm Payment
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
